vector<vector<int>> adj;
vector<bool> visited;
vector<int> dp;

int dfs(int node) {
    if (visited[node]) return dp[node];
    visited[node] = true;

    int maxPath = 1;
    for (int neighbor : adj[node]) {
        maxPath = max(maxPath, 1 + dfs(neighbor));
    }

    return dp[node] = maxPath;
}

